  .navbar-main.show .navbar-main__collapse {
    display: block;
    width: 100%;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    top: 100%;
    z-index: 15;
    background-color: #131213;
    padding-bottom: 65px;
