  align-items: center;
  justify-content: center;
}

.menu {
  width: 90%;
  height: 2px;
  border-radius: 10px;
  background: var(--menu-color);
  transform: translateX(0);
  transform: rotate(0);
  transform-origin: 0% 50%;
  opacity: 1;
  transition: 0.3s all;
}

@media (max-width: 767.98px) {
