  }
}

.footer-nav {
  padding: 0;
  list-style: none;
}
.footer-nav_light .footer-nav__link {
  font-weight: 500;
  color: #fff;
}
.footer-nav .nav-item {
  padding: 0.5rem 0;
  font-size: 0;
}
.footer-nav .nav-item:first-child .footer-list__link {
  font-weight: 500;
  color: #fff;
}
.footer-nav__link {
  position: relative;
  font-size: calc(12rem / 16);
  font-weight: 400;
  padding: 0 0 4px;
  color: #CFC5C5;
}
.footer-nav__link:before {
  content: "";
  position: absolute;
  bottom: 0;
  width: 100%;
  height: 1px;
  background-color: #D44E49;
  left: -10px;
  opacity: 0;
  transition: all 0.1s;
}
.footer-nav__link:hover, .footer-nav__link:focus {
  color: #fff;
  text-decoration: none;
}
.footer-nav__link:hover:before, .footer-nav__link:focus:before {
  left: 0;
  opacity: 1;
  transition: all 0.2s;
}
.footer-nav__link__pr-30 {
  padding-right: 30px;
}

.footer-nav {
  padding-left: 0;
}
.footer-nav__conf {
